import { GoogleMap, Marker, useLoadScript } from "@react-google-maps/api";
import { useMemo } from "react";
import React from "react";
import "../../assets/css/App.css";

const Maps = () => {
  const [allcase, setAllcase] = React.useState();
  React.useEffect(() => {
    async function getJSON() {
      const response = await fetch("http://localhost:3333/getallcase", {
        method: "GET", // or 'PUT'
      });

      const sum = await response.json();

      setAllcase(sum);
    }
    getJSON();
  }, []);
  const { isLoaded } = useLoadScript({
    googleMapsApiKey:"AIzaSyBJNF1WF1PZ072rIlzasimj-wEVnaCu2WY",
  });
  const center = useMemo(() => ({ lat: 13.7884586, lng: 100.6083957}), []);

  const onLoad = (map) => {
    const bounds = new google.maps.LatLngBounds();
    allcase?.forEach(({ lat, lng }) => bounds.extend({ lat, lng }));
    map.fitBounds(bounds);
  };

  return (
    <div className="App">
      {!isLoaded ? (
        <h1>Loading...</h1>
      ) : (
        <GoogleMap mapContainerClassName="map-container" onLoad={onLoad}  center={center}
        zoom={10}>
          {allcase.map(({ lat, lng }) => (
            <Marker position={{ lat, lng }} />
          ))}
        </GoogleMap>
      )}
    </div>
  );
};

export default Maps;
