import { GoogleMap, Marker, useLoadScript } from "@react-google-maps/api";
import { useMemo } from "react";
import React from "react";
import "../../assets/css/App.css";

export default function Maps()  {
  const [allcase, setAllcase] = React.useState();
  React.useEffect(() => {
    async function getJSON() {
      let arr = [];
      const response = await fetch("http://localhost:3333/getmap", {
        method: "GET", // or 'PUT'
      });

      const sum = await response.json();
      for(let i=0;i<sum.length;i++ ){
        const obj = {lat :parseFloat(sum[i].lat),lng :parseFloat(sum[i].lng)};

        arr.push(obj);
      }
      setAllcase(arr);
    }
    getJSON();
  }, []);
  React.useEffect(() => {
   console.log(allcase);
  }, [allcase]);
  const { isLoaded } = useLoadScript({
    googleMapsApiKey:"AIzaSyBJNF1WF1PZ072rIlzasimj-wEVnaCu2WY",
  });
  const markers = [
    { lat: 18.5204, lng: 73.8567 },
    { lat: 18.5314, lng: 73.8446 },
    { lat: 18.5642, lng: 73.7769 },
  ];

  const onLoad = (map) => {

    const bounds = new google.maps.LatLngBounds();
    markers?.forEach(({ lat, lng }) => bounds.extend({ lat, lng }));
    map.fitBounds(bounds);
  };

  return (
    <div className="App">
      {!isLoaded ? (
        <h1>Loading...</h1>
      ) : (
        <GoogleMap mapContainerClassName="map-container" onLoad={onLoad}>
          {markers.map(({ lat, lng }) => (
            <Marker position={{ lat, lng }} />
          ))}
        </GoogleMap>
      )}
    </div>
  );
};
